<include file="Public:header"/><style type="text/css">    .green {        color: green;    }    .red {        color: red;    }</style><div class="mainbox">    <div id="nav" class="mainnav_title">        <ul>            <a href="{pigcms{:U('Express/index')}" class="on">快递公司列表</a>|            <a href="javascript:void(0);"               onclick="window.top.artiframe('{pigcms{:U('Express/add')}','添加快递公司',520,250,true,false,false,addbtn,'add',true);">添加快递公司</a>        </ul>    </div>    <form name="myform" id="myform" action="" method="post">        <div class="table-list">            <table width="100%" cellspacing="0">                <thead>                <tr>                    <th>编号</th>                    <th>公司名称</th>                    <th>公司代码</th>                    <th>公司网址</th>                    <th>添加时间</th>                    <th>状态</th>                    <th class="textcenter">操作</th>                </tr>                </thead>                <tbody>                <if condition="is_array($expresses)">                    <volist name="expresses" id="express">                        <tr>                            <td>{pigcms{$express.id}</td>                            <td>{pigcms{$express.name}</td>                            <td>{pigcms{$express.code}</td>                            <td><a href="{pigcms{$express.url}" target="_blank">{pigcms{$express.url|rtrim='/'}</a></td>                            <td>{pigcms{$express.add_time|date='Y-m-d H:i:s',###}</td>                            <td>                                <if condition="$express['status']"><span class="green">启用</span>                                    <else/>                                    <span class="red">关闭</span></if>                            </td>                            <td class="textcenter"><a href="javascript:void(0);"                                                      onclick="window.top.artiframe('{pigcms{:U('Express/edit',array('id'=>$express['id'],'frame_show'=>true))}','查看详细信息',520,250,true,false,false,false,'add',true);">查看</a>                                | <a href="javascript:void(0);"                                     onclick="window.top.artiframe('{pigcms{:U('Express/edit',array('id'=>$express['id']))}','编辑快递公司信息',520,250,true,false,false,editbtn,'add',true);">编辑</a>                                | <a href="javascript:void(0);" class="delete_row"                                     parameter="id={pigcms{$express.id}" url="{pigcms{:U('Express/del')}">删除</a>                            </td>                        </tr>                    </volist>                    <tr>                        <td class="textcenter pagebar" colspan="8">{pigcms{$page}</td>                    </tr>                    <else/>                    <tr>                        <td class="textcenter red" colspan="8">列表为空！</td>                    </tr>                </if>                </tbody>            </table>        </div>    </form></div><include file="Public:footer"/>