<include file="Public:header"/><div class="mainbox">    <div id="nav" class="mainnav_title">        <ul>            <a href="{pigcms{:U('Meal/order')}" class="on">订单列表</a>        </ul>    </div>    <form name="myform" id="myform" action="" method="post">        <div class="table-list">            <table width="100%" cellspacing="0">                <colgroup>                    <col/>                    <col/>                    <col/>                    <col/>                    <col/>                    <col width="180" align="center"/>                </colgroup>                <thead>                <tr>                    <th>编号</th>                    <th>商家名称</th>                    <th>店铺名称</th>                    <th>订餐人</th>                    <th>电话</th>                    <th>下单时间</th>                    <th>总价</th>                    <th>状态</th>                    <th class="textcenter">操作</th>                </tr>                </thead>                <tbody>                <if condition="is_array($order_list)">                    <volist name="order_list" id="vo">                        <tr>                            <td>{pigcms{$vo.order_id}</td>                            <td>{pigcms{$vo.merchant_name}</td>                            <td>{pigcms{$vo.store_name}</td>                            <td>{pigcms{$vo.name}</td>                            <td>{pigcms{$vo.phone}</td>                            <td>{pigcms{$vo.dateline|date="Y-m-d H:i:s",###}</td>                            <td>￥{pigcms{$vo.price}</td>                            <td>                                <if condition="$vo['statu'] eq 2">已评论                                    <else/>                                    未评论                                </if>                            </td>                            <td class="textcenter">                                <a href="javascript:void(0);"                                   onclick="window.top.artiframe('{pigcms{:U('Meal/order_detail',array('order_id'=>$vo['order_id'],'frame_show'=>true))}','查看订餐详情',480,260,true,false,false,false,'detail',true);">查看</a>                            </td>                        </tr>                    </volist>                    <else/>                    <tr>                        <td class="textcenter red" colspan="6">列表为空！</td>                    </tr>                </if>                </tbody>            </table>        </div>    </form></div><include file="Public:footer"/>