<include file="Public:header"/><style type="text/css">    .c-gray {        color: #999;    }    .table-list tfoot tr {        height: 40px;    }    .green {        color: green;    }    a, a:hover {        text-decoration: none;    }    .click_show {        color: #498CD0;    }</style><script type="text/javascript">    $(function () {        $('.status-enable > .cb-enable').click(function () {            if (!$(this).hasClass('selected')) {                var url = window.location.href;                var type_id = $(this).data('id');                $.post("<?php echo U('Product_property/propertytype_status'); ?>", {                    'type_status': 1,                    'type_id': type_id                }, function (data) {                    window.location.href = url;                })            }            if (parseFloat($(this).data('status')) == 0) {                $(this).removeClass('selected');            }            return false;        })        $('.status-disable > .cb-disable').click(function () {            if (!$(this).hasClass('selected') && parseFloat($(this).data('status')) == 1) {                var url = window.location.href;                var type_id = $(this).data('id');                if (!$(this).hasClass('selected')) {                    $.post("<?php echo U('Product_property/propertytype_status'); ?>", {                        'type_status': 0,                        'type_id': type_id                    }, function (data) {                        window.location.href = url;                    })                }            }            return false;        })    })</script><div class="mainbox">    <div id="nav" class="mainnav_title">        <ul>            <a href="{pigcms{:U('Sys_product_property/propertyType')}" class="on">商品栏目属性分类</a>|            <a href="javascript:void(0);"               onclick="window.top.artiframe('{pigcms{:U('Sys_product_property/propertyType_add')}','添加商品栏目属性分类',480,310,true,false,false,addbtn,'add',true);">添加商品栏目属性分类</a>        </ul>    </div>    <table class="search_table" width="100%">        <tr>            <td>                <form action="{pigcms{:U('Sys_product_property/propertyType')}" method="get">                    <input type="hidden" name="c" value="Property"/>                    <input type="hidden" name="a" value="property"/>                </form>            </td>        </tr>    </table>    <div class="table-list">        <table width="100%" cellspacing="0">            <thead>            <tr>                <th>删除 | 修改</th>                <th>编号</th>                <th>名称</th>                <th>查看属性</th>                <th>状态</th>                <th class="textcenter" width="100">操作</th>            </tr>            </thead>            <tbody>            <if condition="is_array($propertytype)">                <volist name="propertytype" id="propertytype">                    <tr>                        <td>                            <a url="<?php echo U('Sys_product_property/propertyType_del', array('type_id' => $propertytype['type_id'])); ?>"                               class="delete_row"><img src="{pigcms{$static_path}images/icon_delete.png" width="18"                                                       title="删除" alt="删除"/></a> | <a href="javascript:void(0);"                                                                                      onclick="window.top.artiframe('{pigcms{:U('Sys_product_property/propertyType_edit', array('type_id' => $propertytype['type_id']))}','修改分类 - {pigcms{$propertytype.type_name}',480,<if condition="$propertytype['cat_pic']">390                            <else/>                            310            </if>            ,true,false,false,editbtn,'edit',true);"><img src="{pigcms{$static_path}images/icon_edit.png" width="18"                                                          title="修改" alt="修改"/></a></td>            <td>{pigcms{$propertytype.type_id}</td>            <td><a href="javascript:void(0);"                   onclick="window.top.artiframe('{pigcms{:U('Product/category_edit', array('id' => $propertytype['cat_id']))}','修改分类 - {pigcms{$propertytype.type_name}',480,<if condition="$propertytype['type_name']">390                <else/>                310</if>,true,false,false,editbtn,'edit',true);"><?php if ($propertytype['cat_level'] > 1) {                    echo str_repeat('|——', $propertytype['cat_level']);                } ?> <span <?php if ($propertytype['cat_level'] == 1){ ?>style="font-weight: bold;" <?php } ?>>{pigcms{$propertytype.type_name}</span></a>            </td>            <td>                [<a href="<?php echo U('Sys_product_property/property', array('poroerty_type_id' => $propertytype['type_id'])) ?>"                    class="click_show">点击查看属性</a>]            </td>            <td>                <if condition="$propertytype['type_status'] eq 1"><span class="green">启用</span>                    <else/>                    <span class="red">禁用</span></if>            </td>            <td>                <span class="cb-enable status-enable" data-id="<?php echo $propertytype['type_id']; ?>"><label                        class="cb-enable <if condition="$propertytype['type_status'] eq 1">selected</if>                    " data-id="<?php echo $propertytype['type_id']; ?>" data-status="{pigcms{$propertytype.type_status}"><span>启用</span><input                        type="radio" name="status" value="1" <if condition="$propertytype['type_status'] eq 1">                        checked="checked"                    </if> /></label></span>                <span class="cb-disable status-disable" data-id="<?php echo $propertytype['type_id']; ?>"><label                        class="cb-disable <if condition="$propertytype['type_status'] eq 0">selected</if>                    " data-id="<?php echo $propertytype['type_id']; ?>"data-status="{pigcms{$propertytype.type_status}"><span>禁用</span><input                        type="radio" name="status" value="0" <if condition="$propertytype['type_status'] eq 0">                        checked="checked"                    </if>/></label></span>            </td>            </tr>            </volist>            </if>            </tbody>            <tfoot>            <if condition="is_array($propertytype)">                <tr>                    <td class="textcenter pagebar" colspan="7">{pigcms{$page}</td>                </tr>                <else/>                <tr>                    <td class="textcenter red" colspan="7">列表为空！</td>                </tr>            </if>            </tfoot>        </table>    </div></div><include file="Public:footer"/>